package main

import (
	"fmt"
	"sort"
)

func threeSum(nums []int) [][]int {
	res := make([][]int, 0)
	// 排序
	sort.Ints(nums)
	// 全是正数或者全部负数直接return
	if len(nums) < 3 || nums[0] > 0 || nums[len(nums)-1] < 0 {
		return res
	}
	// map用于去重, 因为nums是排序之后的， 所以同样的返回值key唯一
	resMap := make(map[[3]int]struct{})
	// 找到正负交界线, 既然相加等于0， 说明正负数都有
	midIdx := 0
	for i := 0; i < len(nums)-1; i++ {
		if nums[i] < 0 && nums[i+1] >= 0 {
			midIdx = i + 1
		}
	}

	// 遍历， 至少有一个数会小于0
	for i := 0; i <= midIdx; i++ {
		// 与上一个相同就不需要比较了
		if (i-1) >= 0 && nums[i] == nums[i-1] {
			continue
		}
	label1:
		for j := i + 1; j < len(nums); j++ {
			need := 0 - (nums[i] + nums[j])
			// 所需的值已经小于nums[j], 已经不需要遍历后面的了
			if need < nums[j] {
				break label1
			}
			if _, ok := resMap[[3]int{nums[i], nums[j], need}]; ok {
				continue
			}
			z := midIdx
			// j 大于midIdx, 说明已经有正数被遍历过了， 直接从j开始
			if j >= midIdx {
				z = j + 1
			}
			for ; z < len(nums); z++ {
				if need == nums[z] {
					res = append(res, []int{nums[i], nums[j], nums[z]})
					resMap[[3]int{nums[i], nums[j], nums[z]}] = struct{}{}
					break
				}
				// 减少不必要的遍历
				if nums[z] > need {
					break
				}
			}
		}
	}
	return res
}

