package main

import (
	"fmt"
	"strconv"
)

func largestPalindromic(num string) string {
	numMap := make([]int, 10)

	for i := 0; i < len(num); i++ {
		c, _ := strconv.Atoi(string(num[i]))
		numMap[c] += 1
	}

	res := make([]int, 0)
	for i := 9; i >= 0; i-- {
		if len(res) == 0 && i == 0 {
			break
		}
		for numMap[i] >= 2 {
			res = append(res, i)
			numMap[i] -= 2
		}
	}
	p := len(res)-1

	for i := 9; i >= 0; i-- {
		if numMap[i] > 0 {
			res = append(res, i)
			break
		}
	}

	for i := p; i >=0; i-- {
		res = append(res, res[i])
	}

	ret := ""
	for _, i := range res {
		ret += strconv.Itoa(i)
	}

	return ret
}

func main()  {
	fmt.Println(largestPalindromic(""))
}